import os
import unittest

import app as testapi
from app import application
import pytest
import json

base64 = 'data:image/png;base64,'
base64jpg = 'data:image/jpeg;base64,'
base64light = 'data:image/png;base64,'


class ApiTests(unittest.TestCase):
    def setUp(self):
        application.config['TESTING'] = True
        self.app = application.test_client()

    def tearDown(self):
        pass

    def test_ping_pong(self):
        response = self.app.get('/ping')
        content = response.data.decode("utf-8")
        self.assertEqual(response.status_code, 200)
        self.assertEqual(content, "pong")

    def test_valid_image_creates_words(self):
        response = self.app.post('/api/v1/base64', data=json.dumps(dict(base64=base64)), content_type='application/json')
        content = response.json
        self.assertEqual(response.status_code, 200)
        self.assertTrue(len(content['imageText']) > 5, "image did not return more than 5 words")
        self.assertTrue(content['inverted'], "image was not inverted")

    def test_valid_image_is_not_inverted_when_light(self):
        response = self.app.post('/api/v1/base64', data=json.dumps(dict(base64=base64light)), content_type='application/json')
        content = response.json
        self.assertEqual(response.status_code, 200)
        self.assertTrue(len(content['imageText']) == 0, "non word image returned words")
        self.assertFalse(content['inverted'], "image was inverted")

    def test_invalid_image_should_return_400(self):
        not_base_64 = "notbase64"
        error_message = "Not a valid base64 string"
        response = self.app.post('/api/v1/base64', data=json.dumps(dict(base64=f'data:image/png;base64,{not_base_64}')), content_type='application/json')
        content = response.json
        self.assertEqual(response.status_code, 400)
        self.assertEqual(content['meta'], not_base_64)
        self.assertEqual(content['error'], error_message)


class ServiceTests(unittest.TestCase):

    def test_happy_base64_encoding_png(self):
        filename = testapi.create_local_image(base64)
        self.assertIsNotNone(filename)
        self.assertIn(".png", filename)
        self.assertNotIn(".jpg", filename)
        os.remove(filename)

    def test_happy_base64_encoding_jpg(self):
        filename = testapi.create_local_image(base64jpg)
        self.assertIsNotNone(filename)
        self.assertIn(".jpg", filename)
        self.assertNotIn(".png", filename)
        os.remove(filename)

    def test_not_able_to_index_base64(self):
        with pytest.raises(testapi.InvalidUsage):
            testapi.create_local_image("notbase64")

    def test_invalid_base64(self):
        with pytest.raises(testapi.InvalidUsage):
            testapi.create_local_image("data:image/png;base64,notbase64")


if __name__ == "__main__":
    unittest.main()



